import React, { useState } from 'react';
import Card from "./components/UI/Card";
import NewUser from "./components/NewUser/NewUser";
import Users from "./components/User/Users";
import ErrorModal from "./components/Modal/ErrorModal";

function App() {
  const [users, setUsers] = useState([]);
  const [errorModalVisible, setErrorModalVisible] = useState(false);
  let errorModal;

  function onModalCloseHandler() {
    setErrorModalVisible(false);
  }

  function userDataSubmitHandler(userData) {
    switch (userData) {
      case (userData.usernameIsValid() && userData.ageIsValid()):
        setUsers(prevState => {
          return [...prevState, userData];
        });
        break;
      case (userData.usernameIsValid() && !userData.ageIsValid()):
        errorModal = <ErrorModal title="Invalid age" onClose={onModalCloseHandler}>
          Please enter a valid age (between 0 and 123).
        </ErrorModal>
        setErrorModalVisible(true);
        break;
      case (!userData.usernameIsValid() && userData.ageIsValid()):
        errorModal = <ErrorModal title="Invalid username" onClose={onModalCloseHandler}>
          Please enter a valid username (one that is not blank).
        </ErrorModal>
        setErrorModalVisible(true);
        break;
      case (!userData.usernameIsValid() && !userData.ageIsValid()):
        errorModal = <ErrorModal title="Invalid input" onClose={onModalCloseHandler}>
          Please enter a valid input.
        </ErrorModal>
        setErrorModalVisible(true);
        break;
      default:
        errorModal = null;
        console.log(`Invalid user state: ${userData.username} ${userData.age}`);
        break;
    }

  }

  return (
    <div>
      <Card>
        <NewUser onSubmitUserData={userDataSubmitHandler}/>
      </Card>
      {errorModal}
      <Users items={users}/>
    </div>
  );
}

export default App;
