import UserList from "./UserList";
import React, { useState } from "react";

function Users(props) {
  const [users, setUsers] = useState([]);

  function userDataSubmitHandler(userData) {
    setUsers(prevState => {
      return [
        ...prevState,
        userData
      ]
    });
  }

  return (
    <UserList users={users} />
  );
}

export default Users;
