import React, { useState } from 'react';
import Card from "./components/UI/Card";
import NewUser from "./components/NewUser/NewUser";
import Users from "./components/User/Users";
import ErrorModal from "./components/Modal/ErrorModal";

function App() {
  const [users, setUsers] = useState([]);
  const [errorModalVisible, setErrorModalVisible] = useState(false);
  const errorModal = null;

  function onModalCloseHandler() {
    setErrorModalVisible(false);
  }

  function userDataSubmitHandler(userData) {
    switch (userData) {
      case (userData.usernameIsValid() && userData.ageIsValid()):
        setUsers(prevState => {
          return [...prevState, userData];
        });
        break;
      case (userData.usernameIsValid() && !userData.ageIsValid()):
        errorModal = <ErrorModal title="Invalid Input" text="Please enter a valid age (> 0)." buttonText="Okay" onClose={onModalCloseHandler} />
        setErrorModalVisible(true);
    }

  }

  return (
    <div>
      <Card>
        <NewUser onSubmitUserData={userDataSubmitHandler}/>
      </Card>
      <Users items={users}/>
    </div>
  );
}

export default App;
